import {Config} from './config'

export default class Graph {

    static async getContacts (token='') {
      let headers = new Headers()
      headers.append('Accept', 'application/json')
      headers.append('Authorization', token)
      let response
      try {
        let url = `${Config.graphUrl}/me/contacts?$select=displayname,businessphones,businessaddress,emailaddresses`
        let options = {
          headers: headers
        }
        response = await this.callGraph(url, options)
        let body = await response.json()
        return this.processContacts(body) 
      } catch (e) {
          throw new Error('Error getting contacts. HTTP: ' + response.status)
      } finally {
      }
    }

    static processContacts (list={})  {
      //let contacts = JSON.parse(list)
      let contact =  []
      let contacts = []
      list.value.forEach((e) => {
          contact.push(e.displayName)
          contact.push(e.emailAddresses[0].address)
          contact.push(e.businessPhones[0])
          contact.push(e.businessAddress.city)
          contact.push(e.businessAddress.state)
          contacts.push(contact)
          contact = []
      })
      return contacts
    }

    static async ensureFileExists (token='') {
      let headers = new Headers()
      headers.append('Accept', 'application/json')
      headers.append('Authorization', token)
      let response
      try {
        let url = Config.excelFilePath
        let options = {
          headers: headers
        }
        response = await this.callGraph(url, options)

        if (response.ok) {
          return 
        }
        else if (response.status === 404) {
          await this.createEmptyExcel(token)
          return 
        }
        else {
          throw new Error()
        }
      } catch (e) {
          throw new Error('Could not set-up Excel file on your drive.')
      } finally {
      }
    }

    static async createEmptyExcel(token='')  {
      let blankXlsxFileBase64Content = ""
      let url = "data:application/octet-stream;base64," + blankXlsxFileBase64Content
      let blob
      try {
        let response = await fetch(url)
        blob = await response.blob()
      } catch (e) {
        throw new Error('Error in creating empty Excel file blob data.')
      }

      let headers = new Headers()
      headers.append('Content-Type', 'application/octet-stream')
      headers.append('Authorization', token)
      try {
        let url = `${Config.excelFilePath}/content`       
        let options = {
          method: 'PUT',
          headers: headers,
          body: blob
        }
        let response = await this.callGraph(url, options)
        if (response.ok) {
          return
        } else { throw new Error()}

      } catch (e) {
          throw new Error('Error creating empty file.')
      }    
    }

    static async loadContactInExcle (token = '', contacts = null) {
      let headers = new Headers()
      headers.append('Accept', 'application/json')
      headers.append('Authorization', token)
      let body = {} //New worksheet with default name
      let response = null
      let url = ''
      let options = {}

      try {
        // Step-1: Create an Excel persistent session 
        body = {
          persistChanges: true 
        }
        url = `${Config.excelFilePath}/workbook/createSession`      
        options = {
          method: 'POST',
          headers: headers,
          body: JSON.stringify(body)
        }
        response = await this.callGraph(url, options)

        if (!response.ok) { throw new Error('Create session failed!. ' + response.status) }
        let session = await response.json()

        // Load the session id as part of the headers. 
        headers.append('Workbook-Session-Id', session.id)
        sessionStorage.setItem('workbookSessionId', session.id)

        // Step-2: Create a new worksheet
        url = `${Config.excelFilePath}/workbook/worksheets`
        options = {
          method: 'POST',
          headers: headers,
          body: JSON.stringify(body)
        }
        response = await this.callGraph(url, options)
        if (!response.ok) { throw new Error('Create workseet failed. ') }
        let ws = await response.json()
        
        // Step-3: Create a new table
        body = {
          address: `${ws.name}!A1:E1`, 
          hasHeaders: true
        }
        url = `${Config.excelFilePath}/workbook/worksheets/${ws.name}/tables/add`
        options = {
          method: 'POST',
          headers: headers,
          body: JSON.stringify(body)
        }
        response = await this.callGraph(url, options)
        if (!response.ok) { throw new Error('Create table failed. ') }
        let table = await response.json()

        // Step-4: Add rows at the end of the table.
        body = {
          index: -1,
          values: contacts
        }
        url = `${Config.excelFilePath}/workbook/worksheets/${ws.name}/tables/${table.name}/rows`
        options = {
          method: 'POST',
          headers: headers,
          body: JSON.stringify(body)
        }
        response = await this.callGraph(url, options)
        if (!response.ok) { throw new Error('Add rows to Excel table failed. ') }
      } catch (e) {
        throw new Error("Error in creating Excel worksheet: " + e)
      }
    }
    
    /*
    * Close the Excel session as part of the logout actions. It helps release the workbook for other user actions such as opening with the Excel client.  
    */
    static async closeSession(token='') {
      let headers = new Headers()
      headers.append('Accept', 'application/json')
      headers.append('Authorization', token)     
      headers.append('Workbook-Session-Id', sessionStorage.getItem('workbookSessionId'))
      try {
        let url = `${Config.excelFilePath}/workbook/closeSession`      
        let options = {
          method: 'POST',
          headers: headers,

        }
        let response = await this.callGraph(url, options)
        return 
      } catch (e) {
        console.log("Error loading user information.");
      } 
    }

    static async getUserInfo (token='') {
      let headers = new Headers()
      headers.append('Accept', 'application/json')
      headers.append('Authorization', token)
      try {
        let url = `${Config.graphUrl}/me`      
        let options = {
          headers: headers
        }
        let response = await this.callGraph(url, options)
        return response
      } catch (e) {
        console.log("Error loading user information.");
      } 
    }    

    static async callGraph (path, options) {
      try {
        let response = await fetch(path, options)
        if (response.ok || response.status === 404) {
          return response
        } else {
          throw new Error()
        }
      } catch (e) {
        throw new Error('Error while calling Microsoft Graph: ' + e)
      }
    }
}


